let folder = context.get("folder");
if (folder===undefined) {
    folder="/";
    context.set("folder", folder);
}

let hidden = context.get("hidden");
if (hidden===undefined) {
    hidden=false;
    context.set("hidden", hidden);
}

if (msg.topic==="up") {
    var the_arr = folder.split('/');
    the_arr.pop();
    folder=the_arr.join('/'); 
    context.set("folder", folder);
}
if (msg.topic==="change") {
    folder=msg.payload;    
    context.set("folder", folder);
}
if (msg.topic==="hidden") {
    hidden=msg.payload;    
 context.set("hidden", hidden);
}

msg.payload = {"start":folder, "hidden": hidden};
return msg;

